document.addEventListener("DOMContentLoaded", function () {
  var menuBar = document.querySelector(".menu-bar");
  var boxMenu = document.querySelector(".box-menu");
  var menuClose = document.querySelector(".menu-close");
  var header = document.getElementById("header");
  var menuLinks = document.querySelectorAll(".menu-section ul li a");
  var progressBar = document.getElementById("myBar");

  menuBar.addEventListener("click", function () {
    boxMenu.style.transform = "translateX(0%)";
    boxMenu.style.transition = "0.3s";
  });

  menuClose.addEventListener("click", function () {
    this.parentNode.style.transform = "translateX(100%)";
  });

  var scroll_start = 0;
  var startchange = document.getElementById("header");
  var offset = startchange.offsetTop;

  if (startchange) {
    window.addEventListener("scroll", function () {
      scroll_start = window.pageYOffset || document.documentElement.scrollTop;
      if (scroll_start > offset) {
        header.style.backgroundColor = "#ffffff";
      } else {
        header.style.backgroundColor = "transparent";
      }
    });
  }

  menuLinks.forEach(function (link) {
    link.addEventListener("click", function (e) {
      e.preventDefault();
      var href = this.getAttribute("href");
      var h = header.offsetHeight;
      window.scrollTo({
        top: document.querySelector(href).offsetTop - h,
        behavior: "smooth",
      });
    });
  });

  window.onscroll = function () {
    var winScroll = document.body.scrollTop || document.documentElement.scrollTop;
    var height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    progressBar.style.top = scrolled + "%";
  };
});
